import React, { Component } from 'react';
import { Container, Row, Col } from 'react-materialize';
import dell from '../../Media/Dell.png';
import { interval } from 'rxjs';


const text = <Col className = 'board'><div className = 'content'><p>Long long time ago..........<br/>sdfasfgasfgfagdfgdfg</p><p>Long long time ago..........<br/>sdfasfgasfgfagdfgdfg</p><p>Long long time ago..........<br/>sdfasfgasfgfagdfgdfg</p><p>Long long time ago..........<br/>sdfasfgasfgfagdfgdfg</p><p>Long long time ago..........<br/>sdfasfgasfgfagdfgdfg</p><p>Long long time ago..........<br/>sdfasfgasfgfagdfgdfg</p><p>Long long time ago..........<br/>sdfasfgasfgfagdfgdfg</p><p>Long long time ago..........<br/>sdfasfgasfgfagdfgdfg</p><p>Long long time ago..........<br/>sdfasfgasfgfagdfgdfg</p><p>Long long time ago..........<br/>sdfasfgasfgfagdfgdfg</p></div></Col>
class HomePage extends Component {
    constructor() {
        super();
        this.state = {
            show: true,
            play: true
        }
    }

    click = () => {
        this.setState(prevState => ({
            show: !prevState.show,
            play: !prevState.play
        }));
        if(!this.state.show) {
            clearInterval(interval);
        }
        let interval = setInterval(this.click(), 5000)
    }

    render() {
        return (
            <Container className = 'home-page center-align'>
            <img src = {dell} className = 'bg' alt = 'Home page' />
               
               <Row className = 'blue-grey-text text-lighten-5 valign-wrapper'>
                 {this.state.play ?    
                    <Col s = {12}>
                        <h1>Hi. I'm Das.</h1>
                        <h2>Software developer from California.</h2>
                    </Col>
                    : text}
                </Row>
                
                <Row className = 'blue-grey-text text-lighten-5'>
                    <Col s={12} className = 'column'>
                    <button onClick = {this.click}>{this.state.show ? <i className = 'far fa-play-circle fa-5x meetme-icon'></i> : <i className = 'far fa-pause-circle fa-5x meetme-icon'></i>}</button>
                    </Col>
                </Row>
            </Container>
        )
    }
}

export default HomePage;
